MyImager.CaptureImage("MyImage_1.jpg", true);

example.code:
private void btnStart_Click(object sender, EventArgs e)
{
   if (!MyImager.AcquisitionStarted)
   {
       //start acquiring images to a picture box/viewfinder set in Config
       MyImager.StartAcquisition();
    }
}

private void btnStart_Click(object sender, EventArgs e)
{
   if (!MyImager.AcquisitionStarted)
   {
       //start acquiring images to a picture box/viewfinder
       MyImager.StartAcquisition(pictureBox1);
    }
}

MyImager = new Imaging2();
